settings.liquid:
{% assign variant_products = product.metafields.custom.variant_picker.value %}
{% if variant_products != blank %}
<style>
  /* Main picker container */
  .custom-variant-picker {
    margin: 20px 0;
  }

  /* Title styling */
  .custom-variant-picker .color-title {
    margin-bottom: 10px;
  }

  /* Container for the variant circles */
  .custom-variant-picker .color-variants {
    display: flex;
    gap: 10px;

  }

  /* Each circular thumbnail */
  .custom-variant-picker .variant-thumbnail {
    width: 40px;
    height: 40px;
    border-radius: 50%;
    overflow: hidden;
    cursor: pointer;
    border: 2px solid transparent;
    transition: border-color 0.3s ease;
    display: flex;
    align-items: center;
    justify-content: center;

    border-color: rgba(0, 0, 0, 0.2); 
  }

  /* Hover outline with reduced opacity */
  .custom-variant-picker .variant-thumbnail:hover {
    border-color: rgba(0, 0, 0, 0.5);
  }

  /* Active variant (matching current product) has a solid black outline */
  .custom-variant-picker .variant-thumbnail.active {
    border-color: black;
  }

  /* Keep the black outline on hover if already active */
  .custom-variant-picker .variant-thumbnail.active:hover {
    border-color: black;
  }

  /* Make the image a bit smaller to create space between the border and the image */
  .custom-variant-picker .variant-thumbnail img {
    width: 80%;
    height: 80%;
    border-radius: 50%;
    object-fit: cover;
  }
</style>

<div class="custom-variant-picker">
  <!-- Title Section -->
  <div class="color-title h4">
    Color: <span class="h4" id="selected-color">
      {% assign selected_img = nil %}
      {% for img in product.images %}
        {% assign alt_lower = img.alt | downcase %}
        {% if alt_lower != "" and alt_lower contains "color" %}
          {% assign selected_img = img %}
          {% break %}
        {% endif %}
      {% endfor %}
      {% if selected_img %}
        {% assign displayed_text = selected_img.alt %}
        {% assign displayed_text = displayed_text | replace: 'Color ', '' %}
        {% assign displayed_text = displayed_text | replace: 'COLOR ', '' %}
        {% assign displayed_text = displayed_text | replace: 'color ', '' %}
        {{ displayed_text | escape }}
      {% else %}
        N/A
      {% endif %}
    </span>
  </div>

  <!-- Variants Section -->
  <div class="color-variants">
    {% for variant in variant_products %}
      {% assign selected_img = nil %}
      <!-- Find the first image with alt text containing "color" -->
      {% for img in variant.images %}
        {% assign alt_lower = img.alt | downcase %}
        {% if alt_lower != "" and alt_lower contains "color" %}
          {% assign selected_img = img %}
          {% break %}
        {% endif %}
      {% endfor %}

      {% if selected_img %}
        <!-- If this product is the current product, highlight with "active" -->
        <div 
          class="variant-thumbnail {% if product.id == variant.id %}active{% endif %}"
          onclick="window.location.href='{{ variant.url }}'"
        >
          <!-- Keep the original alt text for accessibility, but it can still contain "color" -->
          <img 
            src="{{ selected_img | img_url: 'small' }}" 
            alt="{{ selected_img.alt | escape }}"
          >
        </div>
      {% else %}
        <!-- If no image with 'color' in the alt text, show a fallback -->
        <div 
          class="variant-thumbnail {% if product.id == variant.id %}active{% endif %}"
          onclick="window.location.href='{{ variant.url }}'"
        >
          <span>No color image found</span>
        </div>
      {% endif %}
    {% endfor %}
  </div>
</div>
{% endif %}
